// 只用于上传文件

var remote = {
    receiver: 'http://10.152.77.42:8527/',
    rootpath: '/home/work/odp'
};

fis
.media('remote')
.match('*', {
    release: false
})
.match('favicon.ico', {
    release: 'dist',
    url: '/static/boss/$1'
})
.match('dist/(**)', {
    release: '$1',
    url: '/static/boss/$1'
})
.match('dist/**', {
    deploy: [
        fis.plugin('local-deliver'),
        fis.plugin('http-push', {
            receiver: remote.receiver,
            to      : remote.rootpath + '/webroot/static/boss/'
        })
    ]
})
.match('/dist/(index).html', {
    release: '$1',
    rExt: '.tpl',
    deploy: [
        fis.plugin('local-deliver'),
        fis.plugin('http-push', {
            receiver: remote.receiver,
            to      : remote.rootpath + '/template/boss/'
        })
    ]
});

fis
.media('prod')
.match('*', {
    release: false
})
.match('/dist/(**)', {
    release: '/static/boss/$1',
    url: '/static/boss/$1'
})
.match('/dist/(index).html', {
    release: '/template/boss/$1',
    rExt: '.tpl'
});
