import program from 'commander';
import {error, warning, success, info} from './color';

export default (version) => {
    program
        .version(version | '1.0.0')
        .command('dll [ ...[ commander]]', 'build commons to vendor')
        .command('server [ ...[ commander]]', 'release and init service', {isDefault: true})
        .command('mock [ ...[ commander]]', 'init a server for mock')
        .command('deploy [ ...[ commander]]', 'deploy files to server')
        .parse(process.argv);
};
